package Practice;

public class Pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		int row=1;
		while(row<=n)
		{
		int nst=n;
		while(nst>=row)
		{
			System.out.print(" *");
			nst--;
		}
			System.out.println(" ");
			row++;
			
			
			
			
			
			
			
			
			
		}

	}

}
