package Practice;

import java.util.Scanner;

public class patterns {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scn = new Scanner(System.in);
		int n=scn.nextInt();
		int row=1;
		
		while(row<=n)
		{ int nst=1;
			while(nst<=n)
			{
				System.out.print("*");
				nst++;
			}
			
			
			System.out.println();
			row++;
			
		}
		}
			
}
        

        
       
	


